CREATE database gymm_shop;

create table employees (
	id INT (11) NOT NULL auto_increment,
	first_name VARCHAR(50),
	last_name VARCHAR(50),
	age INT(11),
	gender VARCHAR(50),
	height_cm INT (11),
	weight_kg INT (11),
	income_€ INT (11),
	year_of_entry DATE,
	kids VARCHAR(50),
	location VARCHAR(50),
    primary key(id)
);


create table password (
	id INT (11) NOT NULL auto_increment,
	password VARCHAR(50),
	 primary key(id)
);

